import "./App.css";
import { Routes, Route } from "react-router-dom";
import Layout from "./components/Layout";
import Home from "./components/Home";
import Login from "./components/Login";
import { useState } from "react";
function App() {
  const arrUsers=[{name:"Aman",email:"aman@gmail.com",username:"Aman7",password:"Pass@1234",phone:"1234567890",country:"India",city:"Lucknow",pincode:"226001"}];
  const [ArrUsers,setArrUsers]=useState(arrUsers);
  const[id,setId]=useState("navBar")

  const changeId=()=>{
    if(id=="navBar"){
      setId("navBar2")
    }
    else{
      setId("navBar")
    }
  }

  const push=(obj)=>{
    arrUsers.push(obj)
    setArrUsers([...arrUsers])
    console.log(arrUsers)
  }

  return (
    <div className="container">
      <Layout id={id}/>
        <Routes>
           <Route path="/" element={<Home clickHandler={push} arrUsers={ArrUsers}/>} />
           <Route path="/login" clickHandler={changeId} element={<Login arrUsers={ArrUsers}/>} />
        </Routes>
    </div>
  );
}

export default App;
