import React from 'react'

const Question = (props) => {


    const answer =(e)=>{
        let id=document.querySelectorAll("options input :selected")
        console.log(id)
    }
  return (
    <><h3 id="questionNum">Question {props.question.id}</h3>
    <hr/>
    <p>{props.question.question}</p>
    <div id="options"><div><input name="answer" type="radio"/><label>{props.question.optionA}</label></div>
    <div><input name="answer" type="radio"/><label>{props.question.optionB}</label></div>
    <div><input name="answer" type="radio"/><label>{props.question.optionC}</label></div>
    <div><input name="answer" type="radio"/><label>{props.question.optionD}</label></div></div>
    <div id="btns"><button onClick={answer} id="saveNext">SAVE & NEXT</button><button onClick={answer} id='clearResponse'>CLEAR RESPONSE</button><button onClick={answer} id='markReview'>MARK FOR REVIEW</button><button onClick={answer} id='saveMarkReview'>SAVE & MARK FOR REVIEW & NEXT</button><button onClick={answer} id="submitClose">SUBMIT & CLOSE</button></div>
    </>
  )
}

export default Question
