import React, { useState } from 'react'
import RestaurantDetails from './RestaurantDetails'

  let detail;
const RestaurantsList = (props) => {
  const [Detail,setDetail]=useState([])

const clickHandler=(event)=>{
  detail=[];let index="";
  index=event.target.closest("div .item").getAttribute("id")
  console.log(index)
  detail.push(props.matched[index-1])
  setDetail([...detail])
  console.log(Detail[0].id)
}

  return (
    <div id='restaurantsList'>
      <div id="list">
        {props.matched.map((item,i)=>{ return <div id={item.id} key={i} className="item" onClick={clickHandler}><img alt="null" src={item.photograph}/><div className="itemDetails"><p id="paraitemName">{item.name}</p><p id="paraCuisine">{item.cuisine_type}</p><p id='paraPlace'>{item.neighborhood}</p></div></div>})}
      </div>
      {/* <RestaurantDetails detail={Detail} /> */}
    </div>
  )
}

export default RestaurantsList
