import React, { Component, createRef } from 'react'
import ChangeBackground from './ChangeBackground'
class Parent extends Component {
    num=1;
    refInput=createRef();
    constructor(props) {
      super(props)

      this.state = {
         num:this.num,
      }
    }
    clickHandlerNumber=()=>{
        console.log(this.refInput.target)
        // this.num=this.refInput.target.value;
        this.setState({
            num:this.num,
        })
    }
  render() {
    return (
      <>
      <input type="number" ref={this.refInput}></input><button onClick={this.clickHandlerNumber}>Enter</button>
      <p>Enter the number you want to send as a prop</p>
      <ChangeBackground num={this.state.num}/>
      </>
    )
  }
}

export default Parent
