import React, { Component, createRef } from "react";

class List extends Component {
    items=[];
  refItem = createRef();
  constructor(props) {
    super(props)

    this.state = {
       items:this.items,
       empty:"",
    }
  }
  clickHandlerAdd = () => {
    let item=this.refItem.current.value;
    if(item===""){
        this.setState({
            empty:"Please enter a character!"
        })
    }
    else{
        this.items.push(item);
        console.log(this.items)
        this.setState({
            items:this.items,
            empty:""
        })
    }
  };

  render() {
    return (<>
      <div id="items">
        <div>
          <input
            id="item"
            type="text"
            placeholder="Enter New Item"
            ref={this.refItem}
          />
          <button id="btnAdd" onClick={this.clickHandlerAdd}>Add</button>
        </div>
        {this.state.items.map(item=>{<><hr/><div><button className="cross">x</button>{item}</div></>})}
      </div>
      <p id="paraEmpty">{this.state.empty}</p></>
    );
  }
}

export default List;
