import React, { Component } from 'react'

class Stopwatch extends Component {
  hour;minute;second;mil;
 constructor(props) {
   super(props)

   this.state = {
      hour:0,
      minute:0,
      second:0,
      mili:0
   }
 }
  start=()=>{
    setInterval(this.startCountDown,1000)
  }
  startStopwatch=()=>{
    if (mil < 99) {
      mil++;
    }
    else {
      mil = 0;
      if (sec < 59) {
          sec++;
      } else {
          sec = 0;
          if (min < 59) {
              min++;
          } else {
              min = 0;
              if (hr < 59) {
                  hr++;
              }
          }
      }
    }
    this.setState({
        hour:this.hour,
        minute:this.minute,
        second:this.second
    })
  }
    render() {
    return (
      <div>
        <p>{this.state.hour}:{this.state.minute}:{this.state.second}</p>
        <button onClick={this.start}>Start</button>
        <button onClick={this.stop}>Stop</button>
        <button onClick={this.reset}>Reset</button>
      </div>
    )
  }
}

export default Stopwatch
