import React, { Component } from 'react'

class Counter extends Component {
    count=0;
  constructor(props) {
    super(props)

    this.state = {
      count:0,
    }
  }
  update=(operation)=>{
    if(operation==="decrease"){
      this.count--;
    }
    else{
      this.count++;
    }
    this.setState({
        count:this.state.count+1,
        evod:this.evod
     })
  }
    render() {
    return (
      <div>
        <button onClick={(decrease)=>this.update}>-</button>
        <p>{this.state.count}</p>
        <button onClick={(increase)=>this.update}>+</button>
      </div>
    )
  }
}

export default Counter
