import React, { useState } from 'react'
import { Link } from 'react-router-dom'

const Cart = (props) => {
    const[total,setTotal]=useState(props.subtotal+100)
  return (<>
    <Link to="/restaurants"><button id='btnBack'>Back</button></Link>
    <div id="divCart">
        <h2>Cart</h2>
        <table id="cartTable">
            <th>Product</th><th>Price</th><th>Quantity</th><th>Subtotal</th>
                {props.cart.map((item,i)=>{
                    return( <tr><td>{item.name}</td><td>₹{item.price}</td><td>{item.quantity}</td><td>₹{item.subtotal}</td><td><button index={i} onClick={props.Remove}>Remove</button></td></tr>)
                })}
        </table>
        <p>Subtotal: ₹{props.subtotal}</p>
        <p>Taxes: ₹100</p>
        <p>Grand Total:₹{total}</p>
    </div>
    </>
  )
}

export default Cart
