import React from 'react'

const Bill = (props) => {
    const {cart,changeHandler,id}=props;
    let total=0;
  return (
    <div id={id}>
		<div id="cartHead"><h1>Cart---	</h1><button id="btnEmptyCart"><i className="fa-solid fa-trash-can"></i></button></div>
		<table id="tableCart">
        {cart.map((item,i)=>{  total+=(parseFloat(parseFloat(item.price)*parseFloat(item.quantity))); return <tr id={i} key={item.id}><td><img src={item.image}></img></td><td><h3>{item.name}</h3><p>Price: ₹{item.price}.00</p><p>Quantity: <input type='number' defaultValue={1} onChange={changeHandler} className='inpQuantity' min='1' id='inp"+$cartProducts[i].id+"'/></p><p>Total price: ${item.price*item.quantity}.00 </p></td><td id='cross' ><button className='cross' >X</button></td></tr>})}
        </table>
        <hr></hr>
		<strong id="gTotal">Grand total: ₹{total}.00</strong>
	</div>
  )
}

export default Bill
