    }

  return (
    <div id="containerSignLog">
     <div id="signLog">
     <div id="signUp">
        <p className="signLogHeading">Sign Up</p>
        <div>
          <input placeholder="Enter your name" type="text" id="newName"/>
          <input placeholder="Enter your email" type="email" id="newEmail"/>
          <p className="paraError">{Errors.email}</p> 
          <input placeholder="Enter your username" type="text" id="newUsername"/>      
          <input placeholder="Enter new password" type="password" id="newPassword"/>        
          <input placeholder="Enter your number" type="number" id="newPhone"/>  
          <p className="paraError">{Errors.phone}</p> 
          <input placeholder="Enter your country" type="text" id="newCountry"/> 
          <input placeholder="Enter your city" type="text" id="newCity"/>  
          <input placeholder="Enter your pincode" type="number" id="newPincode"/> 
        </div>
        <button onClick={SignUp} id="newSubmit">
          Sign Up
        </button>
      </div>
    </div>
  </div>
  )
}

export default Home
