import React, { Component } from "react";
import Screen from "./Screen";
import Buttons from "./Buttons";
class Calculator extends Component {
  problem = "";
  answer = "";
  flagDecimal = true;
  constructor(props) {
    super(props);

    this.state = {
      problem: "",
      answer: "",
    };
  }
  number = (event) => {
    this.problem += event.target.textContent;
    this.setState({
      problem: this.problem,
    });
  };
  operation = (event) => {
    this.flagDecimal = true;
    let last = this.problem.length - 1;
    if (
      this.problem[last] === "+" ||
      this.problem[last] === "-" ||
      this.problem[last] === "*" ||
      this.problem[last] === "/"
    ) {
      return;
    } else if (
      this.problem === "" &&
      (event.target.textContent === "/" || event.target.textContent === "*")
    ) {
      return;
    } else {
      this.problem += event.target.textContent;
    }
    this.setState({
      problem: this.problem,
    });
  };
  decimal = (event) => {
    if (this.flagDecimal === true) {
      this.problem += event.target.textContent;
    } else {
      return;
    }
    this.setState({
      problem: this.problem,
    });
    this.flagDecimal = false;
  };
  reset = () => {
    this.problem = "";
    this.answer = "";
    this.setState({
      problem: this.problem,
      answer: this.answer,
    });
    this.flagDecimal = true;
  };
  calculate = () => {
    this.answer = eval(this.problem);
    this.problem = "";
    this.setState({
      problem: this.problem,
      answer: this.answer,
    });
  this.flagDecimal = true;
  };
  render() {
    return (
      <div id="calculator">
        <Screen problem={this.state.problem} answer={this.state.answer} />
        <Buttons
          number={this.number}
          operation={this.operation}
          decimal={this.decimal}
          reset={this.reset}
          calculate={this.calculate}
        />
      </div>
    );
  }
}

export default Calculator;
