import React, { Component } from "react";
import picLight from "./light.png";
import picDark from "./light.png";
import picParty from "./party.png";
class Themes extends Component {
  pic = picLight;
  clickHandlerTheme = (event) => {
    if (event.target.innerHTML === "Light") {
      this.pic = picLight;
    } else if (event.target.innerHTML === "Light") {
      this.pic = picDark;
    } else if (event.target.innerHTML === "Party") {
      this.pic = picParty;
    }
  };
  render() {
    return (
      <div id="theme" style={{backgroundImage: this.pic}}>
        React Themes
        <button onClick={this.clickHandlerTheme}>Light</button>
        <button onClick={this.clickHandlerTheme}>Dark</button>
        <button onClick={this.clickHandlerTheme}>Party</button>
      </div>
    );
  }
}

export default Themes;
