import React, { Component } from "react";

class AddItem extends Component {
  render() {
    return (
      <>
        <h2>TODO LIST</h2>
        <h3>Add Item</h3>
        <p>
          <input id="new-task" type="text" />
          <button id="addbtn" onClick={this.handleNewTask}>
            Add
          </button>
        </p>
      </>
    );
  }
}

export default AddItem;
