import React, { Component } from 'react'

class Stopwatch extends Component {
  hr=0;min=0;sec=0;mil=99;
 constructor(props) {
   super(props)

   this.state = {
      hour:0,
      minute:0,
      second:0,
      mili:0
   }
 }
  update=(operation)=>{
    let started,Interval;
    if(started===false){
      if(operation==="start"){
        started=true;
        Interval=setInterval(this.startStopwatch,10)
      }
      else if(operation==="reset"){
        this.setState = {
          hour:0,
          minute:0,
          second:0,
          mili:0
       }
      }
    else{
      if(operation==="stop"){
        clearInterval(Interval)
      }
      else if(operation==="reset"){
        clearInterval(Interval)
        this.setState = {
          hour:0,
          minute:0,
          second:0,
          mili:0
       }
      }
    }
    }
  }
  startStopwatch=()=>{
    if (this.mil < 99) {
      this.mil++;
    }
    else {
      this.mil = 0;
      if (this.sec < 59) {
        this.sec++;
      } else {
        this.sec = 0;
          if (this.min < 59) {
            this.min++;
          } else {
            this.min = 0;
              if (this.hr < 59) {
                this.hr++;
              }
          }
      }
    }
    this.setState({
        hour:this.hr,
        minute:this.min,
        second:this.sec,
        mili:this.mil
    })
  }
    render() {
    return (
      <div>
        <p>{this.state.hour}:{this.state.minute}:{this.state.second}:{this.state.mili}</p>
        <button onClick={()=>this.update("start")}>Start</button>
        <button onClick={()=>this.update("stop")}>Stop</button>
        <button onClick={()=>this.update("reset")}>Reset</button>
      </div>
    )
  }
}

export default Stopwatch
