import React, { useState } from 'react'
import RestaurantDetails from './RestaurantDetails'

  let detail={};
const RestaurantsList = (props) => {
  const [Detail,setDetail]=useState(detail)

const clickHandler=(event)=>{
  detail={};
  let index=0;
  index=event.target.closest("div .item").getAttribute("id")
  detail=props.matched[index-1]
  setDetail({...detail})
}

  return (
    <div id='restaurantsList'>
      <div id="list">
        {props.matched.map((item)=>{ return <div id={item.id} key={item.id} className="item" onClick={clickHandler}><img alt="null" src={item.photograph}/><div className="itemDetails"><p id="paraitemName">{item.name}</p><p id="paraCuisine">{item.cuisine_type}</p><p id='paraPlace'>{item.neighborhood}</p></div></div>})}
      </div>
      <div>
      </div>
      { <RestaurantDetails detail={Detail} /> }
    </div>
  )
}

export default RestaurantsList
