import { useState } from "react";
import "./App.css";
const arrGrocery=[];
const arrVeggies=[];
const arrTravelling=[];
const arrMiscellaneous=[];
let numbers={income:0,expense:0,balance:0}
function App() {
  const[Numbers,setNumbers]=useState(numbers)

  const addIncome=()=>{
    let amt=document.getElementById("incomeAmount").value;
    numbers.income=parseFloat(parseFloat(numbers.income)+parseFloat(amt)).toFixed(2);
    numbers.balance=parseFloat(parseFloat(numbers.balance)+parseFloat(amt)).toFixed(2);
    setNumbers({...numbers})
  }

  const addExpense=()=>{
    let text=document.getElementById("expenseText").value
    let amt=document.getElementById("expenseAmount").value
    let category=document.getElementById("selCategory").value
    if(category>0){
      numbers.expense=parseFloat(parseFloat(numbers.expense)+parseFloat(amt)).toFixed(2);
      numbers.balance=parseFloat(parseFloat(numbers.balance)-parseFloat(amt)).toFixed(2);
      if(category==1){
        arrGrocery.push(text);        
      }
      else if(category==2){
        arrVeggies.push(text);        
      }
      else if(category==3){
        arrTravelling.push(text);        
      }
      else if(category==4){
        arrMiscellaneous.push(text);        
      }
      setNumbers({...numbers})
    }
  }

  return (
    <>
      <h2>Expense Tracker</h2>

      <div className="container">
        <h4>Your Balance</h4>
        <h1 id="balance">{Numbers.balance}</h1>

        <div className="inc-exp-container">
          <div>
            <h4>Income</h4>
            <p id="money-plus" className="money plus">{Numbers.income}</p>
          </div>
          <div>
            <h4>Expense</h4>
            <p id="money-minus" className="money minus">{Numbers.expense}</p>
          </div>
        </div>

        <div className="form-control">
          <h3>Add New Income</h3>
          <div id="incomeInp">
            <input style={{width:"100%"}} type="number" id="incomeAmount" placeholder="Enter amount..." />
          </div>
          <button onClick={addIncome} className="btn">
          Add Income
        </button>
        </div>

        <div className="form-control">
          <h3>Add New Expense</h3>
          <div id="incomeInp">
            <input type="text" id="expenseText" placeholder="Enter text..." />
            <input type="number" id="expenseAmount" placeholder="Enter amount..." />
          </div>
          <select id="selCategory"><option value="0">----Select Category------</option><option value="1">Grocery</option><option value="2">Veggies</option><option value="3">Travelling</option><option value="4">Miscellaneous</option></select>
          <button onClick={addExpense} className="btn">
          Add Expense
        </button>
        </div>


        <h3>Expenses</h3>
        <div className="Categories">
        <div className="header"><h2>Grocery</h2><p>Amount- 300/-</p></div>
        <ul id="list" className="list">
          <li className="minus">
            Cash <span>-$400</span><button className="btnEdit">Edit</button><button className="btnDelete">Delete</button>
          </li>
          <li className="minus">
            Cash <span>-$400</span><button className="btnEdit">Edit</button><button className="btnDelete">Delete</button>
          </li>

        </ul>
        </div>
        <div className="Categories">
        <div className="header"><h2>Veggies</h2><p>Amount- 300/-</p></div>
        <ul id="list" className="list">
          <li className="minus">
            Cash <span>-$400</span><button className="btnEdit">Edit</button><button className="btnDelete">Delete</button>
          </li>
        </ul>
        </div>
        <div className="Categories">
        <div className="header"><h2>Travelling</h2><p>Amount- 300/-</p></div>
        <ul id="list" className="list">
          <li className="minus">
            Cash <span>-$400</span><button className="btnEdit">Edit</button><button className="btnDelete">Delete</button>
          </li>
        </ul>
        </div>
        <div className="Categories">
        <div className="header"><h2>Miscellaneous</h2><p>Amount- 300/-</p></div>
        <ul id="list" className="list">
          <li className="minus">
            Cash <span>-$400</span><button className="btnEdit">Edit</button><button className="btnDelete">Delete</button>
          </li>
        </ul>
        </div>
      </div>
    </>
  );
}

export default App;
