import React, { Component } from "react";
import Screen from "./Screen";
import Buttons from "./Buttons";
class Calculator extends Component {
    problem="";answer="";
   constructor(props) {
     super(props)

     this.state = {
        problem:"",
        answer:"",
     }
   }
    number=(event)=>{
      this.problem+=event.target.textContent;
      this.setState({
        problem:this.problem,
      })
    }
    decimal=(event)=>{
        let flag=false;
      for(let i=0;i<this.problem.length;i++){
        if(this.problem[i]==="."){
            flag=true;
        }
      }

      if(flag===false){
        this.problem+=event.target.textContent;
      }
      else{
        return;
      }
      console.log(this.problem)
    }
    reset=()=>{
      this.problem=""
      console.log(this.problem)
    }
    calculate=()=>{
      // eslint-disable-next-line no-eval
      this.answer=eval(this.problem);
      console.log("answer=",this.answer)
    }
  render() {
    return (
      <div id="calculator">
        <Screen problem={this.state.problem}/>
        <Buttons number={this.number} decimal={this.decimal} reset={this.reset} calculate=
        {this.calculate}/>
      </div>
    );
  }
}

export default Calculator;
