import style from "./Third.module.css";
import picDhamaal from "./dhamaal.png";
import piclaptop from "./laptop.png";
import picMobile from "./mobile.png";
class Third extends Component {
  left = "<";
  right = ">";
  constructor(props) {
    super(props);
    this.state = {
      applyCSS:"ab",

    };
  }
  changeLeft=() =>
    {
      this.setState({
        applyCSS:"leftContainer"
      })
    console.log("applyCSS"+this.state.applyCSS)
  }
  render() {
    return (
      <div id={style.third} className={style.slider}>
        <button id={style.btnLeft} onClick={this.changeLeft}>{this.left}</button>
        <button id={style.btnRight}>{this.right}</button>
        <div className={this.state.applyCSS} >
         <img src={picMobile} className={style.slide} />
         <img src={piclaptop} className={style.slide} />
         <img src={picDhamaal} className={style.slide} /></div>
      </div>
    );


  }
}

export default Third;
