import React, { Component } from "react";
import CompletedList from "./CompletedList";

class TodoList extends Component {
  arrTodo=[];element;arrCompleted=[];index;
    constructor(props) {
      super(props)

      this.state = {
        arrTodo:this.props.arrTodo,
      }

    }
  clickHandler=(event)=>{
    this.index=event.target.getAttribute("id")
  }
  render() {
    return (
      <>
        <h3>Todo</h3>
        <ul id="incomplete-tasks">
          {this.props.arrTodo.map((d,index) =><li ><input type='checkbox' id={index} onClick={this.clickHandler}/><label>{d}</label><input type='text'/><button className='edit'>Edit</button><button className='delete'>Delete</button></li>)}    
        </ul>
        <CompletedList arrCompleted={this.arrCompleted}/>
      </>
    );
  }
}

export default TodoList;
