let gender="";
let refName=createRef();let refEmail=createRef();let refPassword=createRef();let refAddress=createRef();let refPhone=createRef();let refGender=createRef();let refHobbies=createRef();let refImage=createRef();let refDOB=createRef();
function App() {
  const [header, setHeader] = useState(["header","Enter your details----"]);
  const [errors,setErrors]=useState({});

  const changeHandler = (event) => { 
    let id = event.target.getAttribute("id");
    let value = event.target.value;
    let regexName=/^[a-zA-Z ]{2,20}$/;
    let regexPhone = /^\d{10}$/;
    let regexEmail = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/;
    if (id === "stFname") {
      console.log(id)
      if (regexName.test(value) !== true) {
          arrErrors.stname="Enter valid name";
      } else {
          arrErrors.stname="";
      }
    }
    if (id === "stLname") {
      if (regexName.test(value) !== true) {
          arrErrors.stname="Enter valid name";
      } else {
          arrErrors.stname="";
      }
    }
    if(id==="inpDOB"){
      let year=value.slice(0,4)
      if(value===""){ 
        arrErrors.DOB="Please select a date of birth"
      }
      else if(year>2002){
        arrErrors.DOB="Date of birth should be of before 2002"
      }
      else{
        arrErrors.DOB="";
      }
    }
    if(id==="gender"){
      if(value!=""){
       arrErrors.gender=""
      }
    }
    if (id === "inpSiblings") {
      if (regexName.test(value) !== true) {
          arrErrors.sibling="Enter valid name";
      } else {
          arrErrors.sibling="";
      }
    }
    if (id === "Ffname") {
      if (regexName.test(value) !== true) {
          arrErrors.Fname="Enter valid name";
      } else {
          arrErrors.Fname="";
      }
    }
    if (id === "Flname") {
      if (regexName.test(value) !== true) {
          arrErrors.Fname="Enter valid name";
      } else {
          arrErrors.Fname="";
      }
    }
    if (id === "fQualification") {
      if (regexName.test(value) !== true) {
          arrErrors.fQualification="Enter valid name";
      } else {
        arrErrors.fQualification="";
      }
    }
    if (id === "fPhone") {
      if (regexPhone.test(value) !== true) {
          arrErrors.fPhone="Enter valid name";
      } else {
        arrErrors.fPhone="";
      }
    }
    if (id === "fEmail") {
      if (regexEmail.test(value) !== true) {
        arrErrors.fEmail="Enter valid email";
      } else {
        arrErrors.fEmail="";
      }
    }
    if (id === "fOccupation") {
      if (regexEmail.test(value) !== true) {
        arrErrors.fOccupation="Enter valid email";
      } else {
        arrErrors.fOccupation="";
      }
    }
    if (id === "Mfname") {
      if (regexName.test(value) !== true) {
          arrErrors.Mname="Enter valid name";
      } else {
          arrErrors.Mname="";
      }
    }
    if (id === "Mlname") {
      if (regexName.test(value) !== true) {
          arrErrors.Mname="Enter valid name";
      } else {
          arrErrors.Mname="";
      }
    }
    if (id === "mQualification") {
      if (regexName.test(value) !== true) {
          arrErrors.mQualification="Enter valid name";
      } else {
        arrErrors.mQualification="";
      }
    }
    if (id === "mPhone") {
      if (regexPhone.test(value) !== true) {
          arrErrors.mPhone="Enter valid name";
      } else {
        arrErrors.mPhone="";
