import React, { Component } from "react";
import TodoList from "./TodoList";
import CompletedList from "./CompletedList";

class AddItem extends Component {
  arrTodo=[];arrCompleted=[];indexTodo;indexCompleted;
  constructor(props) {
    super(props);

    this.state = {
      newTask: "",
      arrTodo:this.arrTodo,
      arrCompleted:this.arrCompleted,
    };
    this.inputRef = React.createRef();
  }
  changeHandlerTask = () => {
    this.setState({
      newTask:this.inputRef.current.value,
    });
    this.arrTodo.push(this.inputRef.current.value)
    this.inputRef.current.value=""
  };
  clickHandler=(event)=>{
    this.indexTodo=event.target.getAttribute("id")
    this.arrCompleted.push(this.arrTodo[this.indexTodo])
    this.arrTodo.splice(this.indexTodo,1)
    this.setState({
      arrTodo:this.arrTodo,
      arrCompleted:this.arrCompleted,
    })
  }
  clickHandlerUncheck=(event)=>{
    this.indexCompleted=event.target.getAttribute("id")
    this.arrTodo.push(this.arrCompleted[this.indexCompleted])
    this.arrCompleted.splice(this.indexCompleted,1)
    this.setState({
      arrTodo:this.arrTodo,
      arrCompleted:this.arrCompleted,
    })
  }
  clickHandlerDelete=(event)=>{
     console.log(event.target.parentElement.textContent)
  } 
  clickHandlerEdit=()=>{

  }
  render() {
    return (
      <>
        <h2>TODO LIST</h2>
        <h3>Add Item</h3>
        <p>
          <input id="new-task" ref={this.inputRef} type="text" />
          <button id="addbtn" onClick={this.changeHandlerTask}>
            Add
          </button>
        </p>
        <TodoList arrTodo={this.arrTodo} clickHandler={this.clickHandler} clickHandlerDelete={this.clickHandlerDelete} clickHandlerEdit={this.clickHandlerEdit}/>
        <CompletedList arrCompleted={this.arrCompleted} index={this.index} clickHandlerUncheck={this.clickHandlerUncheck} clickHandlerDelete={this.clickHandlerDelete} clickHandlerEdit={this.clickHandlerEdit}/>
      </>
    );
  }
}

export default AddItem;
