import React, { Component } from "react";
import TodoList from "./TodoList";
import CompletedList from "./CompletedList";

class AddItem extends Component {
  arrTodo=[];arrCompleted=[];indexTodo;indexCompleted;
  constructor(props) {
    super(props);

    this.state = {
      newTask: "",
      arrTodo:this.arrTodo,
      arrCompleted:this.arrCompleted,
    };
    this.inputRef = React.createRef();
  }
  changeTaskHandler = () => {
    this.setState({
      newTask:this.inputRef.current.value,
    });
    this.arrTodo.push(this.inputRef.current.value)
    console.log(this.arrTodo)
  };
  clickHandler=(event)=>{
    this.indexTodo=event.target.getAttribute("id")
    this.arrCompleted.push(this.arrTodo[this.index])
    this.arrTodo.splice(this.index,1)
    this.setState({
      arrTodo:this.arrTodo,
    })
  }
  clickHandlerUncheck=(event)=>{

    this.setState({
      arrCompleted:this.arrCompleted,
    })
  }
  render() {
    return (
      <>
        <h2>TODO LIST</h2>
        <h3>Add Item</h3>
        <p>
          <input id="new-task" ref={this.inputRef} type="text" />
          <button id="addbtn" onClick={this.changeTaskHandler}>
            Add
          </button>
        </p>
        <TodoList arrTodo={this.arrTodo} clickHandler={this.clickHandler} />
        <CompletedList arrCompleted={this.arrCompleted} index={this.index} clickHandlerUncheck={this.clickHandlerUncheck}/>
      </>
    );
  }
}

export default AddItem;
