  }
  const clickHandlerCheckbox=(event)=>{
    if(event.target.checked===true){
      arrSel.push(event.target.value)
      console.log(arrSel)
    }
    else{
      for(let i=0;i<arrSel.length;i++){
        if(event.target.value==arrSel[i]){
          arrSel.splice(i,1)
        }
      }
      console.log(arrSel)
    }
    setArrSel(arrSel)
  }
  return (
    <div id="task5">
        <h1>Task 5</h1>
        <input type="checkbox" onClick={clickHandlerCheckbox} value="Football"/><label>Football</label>
        <input type="checkbox" onClick={clickHandlerCheckbox} value="Reading Novels"/><label>Reading Novels</label>
        <input type="checkbox" onClick={clickHandlerCheckbox} value="Coding"/><label>Coding</label>
        <button onClick={clickHandler}>Print selected options</button>
        <p>{ArrSel}</p>
    </div>
  )
}

export default Task5
