import React, { Component, createRef } from 'react'

class BMICalculator extends Component {
    refHeight=createRef();
    refWeight=createRef();
    clickHandlerCalculate=()=>{
        let height=this.refHeight.current.value;
        let weight=this.refWeight.current.value;
    }
  render() {
    return (
      <div id="divContainer">
        <div id="divHead">BMI Calculator</div>
        <div id="divInputs">
            <select onChange={this.changeHandlerUnit}><option>---Select units---</option><option>kg-metres</option><option>lbs-inches</option></select>
            <div><label>Enter weight</label><input ref={this.refHeight} type="number" id="inputHeight"/></div>
            <div><label>Enter height</label><input ref={this.refWeight} type="number" id="inputWeight"/></div>
            <button id="btnCalculate" onClick={this.clickHandlerCalculate}>Calcuate</button>
        </div>
        <div id="divResult">
            <p>18.4</p>
            <p>Underweight</p>
        </div>
      </div>
    )
  }
}

export default BMICalculator
