import React, { Component, createRef } from "react";

class EMIcalculator extends Component {
    constructor(props) {
      super(props)

      this.state = {
         emi:0,
         totalInt:0,
         totalAmt:0,
      }
    }
    refAmt=createRef();
    refInterest=createRef();
    refYears=createRef();
    refMonths=createRef();
    clickHandlerCalculate=()=>{
        let amt=parseInt(this.refAmt.current.value);
        let interest=parseInt(this.refInterest.current.value);
        let years=parseInt(this.refYears.current.value);
        let months=parseInt(this.refMonths.current.value);
        let totalMon=parseInt((years*12)+months);
        let intRateMon=interest/100/12; 
        let interestMon=((amt*intRateMon*(Math.pow(1+intRateMon,totalMon)))/((Math.pow(1+intRateMon,totalMon))-1)).toFixed(2);
        console.log(interestMon)


    }
    clickHandlerReset=()=>{

    }
    clickHandlerDetails=()=>{

    }
  render() {
    return (
      <div id="divContainer">
        <div id="divHead">
          <i className="fa-solid fa-bars"></i>EMI Calculator
          <button id="btnRefresh">
            <i className="fa-solid fa-rotate-right" />
          </button>
        </div>
        <div id="divBody">
          <div id="divInputs">
            <div>
              <input className="inpRadio" type="radio" />
              <p>Loan Amount</p>
              <input type="number" ref={this.refAmt} className="inpNumber" id="inpAmount" />
            </div>
            <div>
              <input className="inpRadio" type="radio" />
              <p>Interest %</p>
              <input type="number" ref={this.refInterest} className="inpNumber" id="inpInterest" />
            </div>
            <div>
              <input className="inpRadio" type="radio" />
              <p>Period</p>
              <div id="divInpPeriod"><input type="number" ref={this.refYears}  className="inpPeriod" id="inpPeriodYears" />
              <input type="number" ref={this.refMonths} className="inpPeriod"  id="inpPeriodMonths" /></div>
            </div>
            <div>
              <input className="inpRadio" type="radio" />
              <p>EMI</p>
              <input type="number" className="inpNumber" id="inpEMI" disabled />
            </div>
          </div>
          <div id="divButtons">
            <button onClick={this.clickHandlerCalculate}>CALCULATE</button>
            <button onClick={this.clickHandlerReset}>RESET</button>
            <button onClick={this.clickHandlerDetails}>DETAILS</button>
          </div>
          <div id="divResult">
            <table id="tabResult">
              <tbody>
                <tr>
                  <td>Monthly EMI</td>
                  <td>{this.state.emi}</td>
                </tr>
                <tr>
                  <td>Total Interest</td>
                  <td>{this.state.totalInt}</td>
                </tr>
                <tr>
                  <td>Total Payment</td>
                  <td>{this.state.totalAmt}</td>
                </tr>
              </tbody>
            </table>
          </div>
        </div>
      </div>
    );
  }
}

export default EMIcalculator;
