import React, { Component } from "react";
import styleLight from './Light.module.css'
import styleDark from './Dark.module.css'
import styleParty from './Party.module.css'
class Themes extends Component {
  class = "";
  constructor(props) {
    super(props)

    this.state = {
       class:this.class,
    }
  }
  clickHandlerTheme = (event) => {
    this.class = event.target.getAttribute("id");
    console.log(this.class)
    this.setState({
        class:this.class,
    })
  };
  render() {
    return (
      <div id="theme" className={this.class}>
        <h1>React Themes</h1>
        <button id="styleLight" onClick={this.clickHandlerTheme}>Light</button>
        <button id="styleDark" onClick={this.clickHandlerTheme}>Dark</button>
        <button id="styleParty" onClick={this.clickHandlerTheme}>Party</button>
      </div>
    );
  }
}

export default Themes;
