import React, { Component } from "react";

class AddItem extends Component {
   constructor(props) {
     super(props)

     this.state = {
        newTask:""
     }
   }
   changeTaskHandler=event=>{
      this.setState({
        newTask:event.target.value,
      })
   }
  render() {
    return (
      <>
        <h2>TODO LIST</h2>
        <h3>Add Item</h3>
        <p>
          <input id="new-task" value={this.state.newTask} type="text" onChange={this.changeTaskHandler} />
          <button id="addbtn"  >
            Add
          </button>
        </p>
      </>
    );
  }
}

export default AddItem;
