import React, { Component } from 'react'
import ChangeBackground from './ChangeBackground'
class Parent extends Component {
    num;

  render() {
    return (
      <>
      <button onClick={this.clickHandlerNumber}>{this.num}</button>
      <p>Click on number to increase it.</p>
      <ChangeBackground num={this.num}/>
      </>
    )
  }
}

export default Parent
