import React, { useState } from 'react'


const RestaurantDetails = (props) => {
  let opHours=[];
  const [OpHours,setOpHours]=useState([opHours])
  for (const key in props.detail.operating_hours) {
    opHours.push(`${key}: ${props.detail.operating_hours[key]}`);
  }
  setOpHours([...opHours])
  return (
    <div id="detail">
        <h2>{props.detail.name}</h2>
        <img alt={props.detail.name} src={props.detail.photograph}/>
        <p>Operational Hours :---</p>
        {OpHours.map((item)=>{<p>{item}</p>})}
          {/* {props.detail.operating_hours.map((item)=>{return <p>{item}</p>})} */}
        <h3>Reviews:</h3>
    </div>
  )
}

export default RestaurantDetails
