import React, { Component, createRef } from 'react'

class BMICalculator extends Component {
    refHeight=createRef();
    refWeight=createRef();

    clickHandlerCalulate=()=>{
      let height=this.refHeight.current.value;
      let weight=this.refWeight.current.value;
      console.log(height,weight)
    }
  render() {
    return (
      <div id="divContainer">
        <div id="divHead">BMI Calculator</div>
        <div id="divInputs">
            <div><label>Enter Height</label><input ref={this.refHeight} id="inpHeight"/></div>
            <div><label>Enter Height</label><input ref={this.refWeight} id="inpHeight"/></div>
            <button id="btnCalculate" onClick={this.clickHandlerCalulate}>Calculate</button>
        </div>
        <div id='divResult'>
            <p>18.6</p>
            <p>Underweight</p>
        </div>
      </div>
    )
  }
}

export default BMICalculator
