import React, { Component } from "react";

class CompletedList extends Component {

  constructor(props) {
    super(props)

    this.state = {
       arrCompleted:this.props.arrCompleted,
    }
  }
  clickHandlerUncheck=(event)=>{
    console.log(event.target.removeAttribute('checked'));
  }
  render() {
    return (
      <>
        <h3>Completed</h3>
        <ul id="completed-tasks">
        {this.props.arrCompleted.map((d,index) =><li ><input type='checkbox' id={index} checked={true} onClick={this.clickHandlerUncheck}/><label>{d}</label><input type='text'/><button className='edit'>Edit</button><button className='delete'>Delete</button></li>)}
        </ul>
      </>
    );
  }
}

export default CompletedList;
