import { useState } from "react";
import "./App.css";
function App() {
  const [header, setHeader] = useState("Enter your details----");
  const [errors,setErrors]=useState({});
  let arrErrors={name:"",email:"",password:"",address:"",phone:""}
  const changeHandler = (event) => { 
    let id = event.target.getAttribute("id");
    let value = event.target.value;
    if (id === "inpName") {
      console.log("Name",value)
      let regexName=/^[a-zA-Z]+(([',. -][a-zA-Z ])?[a-zA-Z]*)*$/
      if (regexName.test(value) !== true) {
          arrErrors.name="Enter valid name";
      } else {

      }
    }
    if (id === "inpEmail") {
      let regexEmail = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/;
      if (regexEmail.test(value) !== true) {
        setErrors({email:"Enter valid email"});
      } else {
        setErrors({email:""});
      }
    }
    if (id === "inpPassword") {
      let regexPassword=/^(?=.*\d)(?=.*[A-Z])(?=.*[a-z])(?=.*[a-zA-Z!#$@^%&? "])[a-zA-Z0-9!#$@^%&?]{8,20}$/;
      if (regexPassword.test(value) !== true) {
        setErrors({password:"Password should contain an uppercase,a lowercase and a special character"});
      } else {
        setErrors({password:""});
      }
    }
    if (id === "inpAddress") {
      let regexAddress=/^[a-zA-Z0-9\s,.'-]{3,}$/
      if (regexAddress.test(value) !== true) {
        setErrors({address:"Enter valid address"});
      } else {
        setErrors({address:""});
      }
    }
    if (id === "inpPhone") {
      let regexPhone = /^\d{10}$/;
      if (regexPhone.test(value) !== true) {
        setErrors({phone:"Phone number should contain 10 digits"});
      } else {
        setErrors({phone:""});
      }
    }
  };
  return (
    <div className="container">
      <div id="header">{header}</div>
      <div>
        <div className="labels">Enter your Name</div>
        <div className="inputs">
          <input onBlur={changeHandler}  type="text" id="inpName" />
        </div>
      </div>
      <p className="paraError">{errors.name}</p>
      <div>
        <div className="labels">Enter your Email</div>
        <div className="inputs">
          <input onBlur={changeHandler}  type="email" id="inpEmail" />
        </div>
      </div>
      <p className="paraError">{errors.email}</p>
      <div>
        <div className="labels">Enter your Password</div>
        <div className="inputs">
          <input onBlur={changeHandler}  type="password" id="inpPassword" />
        </div>
      </div>
      <p className="paraError">{errors.password}</p>
      <div>
        <div className="labels">Enter your Address</div>
        <div className="inputs">
          <textarea id="inpAddress" />
        </div>
      </div>
      <p className="paraError">{errors.address}</p>
      <div>
        <div className="labels">Enter your Mobile</div>
        <div className="inputs">
          <input onBlur={changeHandler}  type="number" id="inpPhone" />
        </div>
      </div>
      <p className="paraError">{errors.phone}</p>
      <div>
        <div className="labels">Select your Gender</div>
        <div className="inputs">
          <input onBlur={changeHandler}  type="radio" value="Male" />
          Male
          <input onBlur={changeHandler}  type="radio" value="Female" />
          Female
        </div>
      </div>
      <p className="paraError">Enter</p>
      <div>
        <div className="labels">Enter your Hobbies</div>
        <div className="inputs">
          <input onBlur={changeHandler}  type="checkbox" value="Cricket" />Cricket
          <input onBlur={changeHandler}  type="checkbox" value="Singing" />Singing
          <input onBlur={changeHandler}  type="checkbox" value="Dancing" />Dancing
        </div>
      </div>
      <p className="paraError">Enter</p>
      <div>
        <div className="labels">Choose your Profile Pic</div>
        <div className="inputs">
          <input onBlur={changeHandler}  type="file" />
        </div>
      </div>
      <p className="paraError">Enter</p>
      <div>
        <div className="labels">Select your DOB</div>
        <div className="inputs">
          <select></select>
          <select></select>
          <select></select>
        </div>
      </div>
      <p className="paraError">Enter</p>
      <div id="footer">
        <button id="btnRegister">Register Me</button>
        <button id="btnReset">Reset</button>
      </div>
    </div>
  );
}

export default App;
