//     clearInterval(Interval)
//   }

// })

  const handleClick = () => {
    clearInterval(Interval)
    root.render(
      <div id="parent">
        <div id="stopwatch">
          <p id="stopHour">{hr}</p>
        :
        <p id="stopMinute">{min}</p>
        :
        <p id="stopSecond">{sec}</p>:
        <p  id="stopMilisecond">{mil}</p>
        </div>
        <div id="stopwatchBtn">
        <button id="btnStopwatch" onClick={handleClick}>Stop</button></div></div>

        )
  }


function Counter(){


  if (mil < 99) {
    mil++;
  }
  else {
    mil = 0;
    if (sec < 59) {
        sec++;
    } else {
        sec = 0;
        if (min < 59) {
            min++;
        } else {
            min = 0;
            if (hr < 59) {
                hr++;
            }
        }
    }
  }
  console.log(hr,min,sec,mil);

  root.render(
    <div id="parent">
      <div id="stopwatch">
        <p id="stopHour">{hr}</p>
      :
      <p id="stopMinute">{min}</p>
      :
      <p id="stopSecond">{sec}</p>:
      <p  id="stopMilisecond">{mil}</p>
      </div>
      <div id="stopwatchBtn">
      <button id="btnStopwatch" onClick={handleClick}>Start</button></div></div>

      )

}

