import React, { Component } from "react";
import TodoList from "./TodoList";

class AddItem extends Component {
  arrTodo=[];
  constructor(props) {
    super(props);

    this.state = {
      newTask: "",
    };
    this.inputRef = React.createRef();
  }
  changeTaskHandler = (event) => {
    this.setState({
      newTask:this.inputRef.current.value,
    });
    this.arrTodo.push(this.inputRef.current.value)
    console.log(this.arrTodo)
  };
  render() {
    return (
      <>
        <h2>TODO LIST</h2>
        <h3>Add Item</h3>
        <p>
          <input id="new-task" ref={this.inputRef} type="text" />
          <button id="addbtn" onClick={this.changeTaskHandler}>
            Add
          </button>
        </p>
        <TodoList arrTodo={this.arrTodo} />
      </>
    );
  }
}

export default AddItem;
