import picMobile from "./mobile.png";
class Third extends Component {
  left = "<";
  right = ">";
  constructor() {
    super();
    this.state = {
      applyCSS:"ab",

    }
  }
  changeLeft() {

    console.log("applyCSS"+this.state.applyCSS)
  }
  render() {
    return (
      <div id={style.third} className={style.slider}>
        <button id={style.btnLeft} onClick={this.changeLeft}>
          {this.left}
        </button>
        <button id={style.btnRight}>{this.right}</button>
        <div id={style.container} >
         <img src={picMobile} className={style.slide} />
         <img src={piclaptop} className={style.slide} />
         <img src={picDhamaal} className={style.slide} /></div>
      </div>
    );

