import React, { useState } from 'react'


const RestaurantDetails = (props) => {
  // let opHours=[];

  // const [OpHours,setOpHours]=useState([opHours])
  // for (const key in props.detail.operating_hours) {
  //   opHours.push(`${key}: ${props.detail.operating_hours[key]}`);
  // }
  // setOpHours([...opHours])
  // console.log(props.detail.operating_hours)
  let opHours=Object.entries(props.detail.operating_hours);
  console.log(opHours)
  return (
    <div id="detail">
        <h2>{props.detail.name}</h2>
        <img alt={props.detail.name} src={props.detail.photograph}/>
        <p>Operational Hours :---</p>
        {opHours.map((item)=><p>{item[0]}-{item[1]}</p>)}
        <h3>Reviews:</h3>
        {props.detail.reiews.map((item)=>{ return <div><p>{item.name}</p><p>{item.date}</p><p>{item.rating}</p><p>{item.comments}</p></div>})}
    </div>
  )
}

export default RestaurantDetails
