import React, { Component } from 'react'
import ChangeBackground from './ChangeBackground'
class Parent extends Component {
    num=1;
    constructor(props) {
      super(props)

      this.state = {
         num:this.num,
      }
    }
    clickHandlerNumber=()=>{
        this.num++;
        this.setState({
            num:this.num,
        })
    }
  render() {
    return (
      <>
      <button onClick={this.clickHandlerNumber}>{this.state.num}</button>
      <p>Enter the number you want to sent as a prop</p>
      <ChangeBackground num={this.state.num}/>
      </>
    )
  }
}

export default Parent
