import React, { Component, createRef } from "react";

class List extends Component {
  refId = createRef();
  refName=createRef();
  refPrice=createRef();
  products=[];
  constructor(props) {
    super(props)

    this.state = {
       products:"",
       empty:"",
    }
  }
  clickHandlerAdd=()=>{
    let id=this.refId.current.value;
    console.log(this.refId.current.value)
    let name=this.refName.current.value;
    let price=this.refPrice.current.value;
    if(id===""||name===""||price===""){
       this.setState({
        empty:"Please enter all the fields!"
       })
    }
    else{
        let obj={};
        obj={
            id:id,
            name:name,
            price:price
        };
        this.products.push(obj)
        this.setState({
            product:this.products,
            empty:"",
        })
    }
  }
  render() {
    return (
      <>
        <div id="form">
          <div id="id">
            <label>Product Id</label>
            <input type="number" id="idinput" />
          </div>
          <div id="name">
            <label>Product Name</label>
            <input type="text" id="nameinput" />
          </div>
          <div id="price">
            <label>Product Price</label>
            <input type="number" id="priceinput" />
          </div>
          <button onClick={this.clickHandlerAdd}>ADD PRODUCT</button>
          <p id="empty">{this.state.empty}</p>
        </div>
        <table id="product">
          <tr>
            <th>Product Id</th>
            <th>Product Name</th>
            <th>Product Price</th>
          </tr>
            {/* {this.state.products.map(item=>{<tr><td>{item.id}</td><td>{item.name}</td><td>{item.price}</td></tr>})} */}
        </table>
      </>
    );
  }
}

export default List;
