import React from 'react'

const Question = (props) => {


    const answer =(e)=>{
        let id=document.querySelectorAll('#options input[name="answer"]:checked');
        answer=id[0].value
    }
  return (
    <><h3 id="questionNum">Question {props.question.id}</h3>
    <hr/>
    <p>{props.question.question}</p>
    <div id="options">
    <input name="answer" value={props.question.optionA} type="radio"/><label>{props.question.optionA}</label>
    <input name="answer" value={props.question.optionB} type="radio"/><label>{props.question.optionB}</label>
    <input name="answer" value={props.question.optionC} type="radio"/><label>{props.question.optionC}</label>
    <input name="answer" value={props.question.optionD} type="radio"/><label>{props.question.optionD}</label></div>
    <div id="btns"><button onClick={answer} id="saveNext">SAVE & NEXT</button><button onClick={answer} id='clearResponse'>CLEAR RESPONSE</button><button onClick={answer} id='markReview'>MARK FOR REVIEW</button><button onClick={answer} id='saveMarkReview'>SAVE & MARK FOR REVIEW & NEXT</button><button onClick={answer} id="submitClose">SUBMIT & CLOSE</button></div>
    </>
  )
}

export default Question
