import React, { Component, createRef } from "react";
class SignLog extends Component {
  arrUsers = [];
  upIn=false;refNewEmail=createRef();refNewPhone=createRef();refNewPassword=createRef();refOldEmailPhone=createRef();refOldPassword=createRef();
  constructor(props) {
    super(props)

    this.state = {
       signUpId:"signUp",
       logInId:"logInHidden",
       paraNewEmail:"",
       paraNewPhone:"",
       paraNewPassword:"",
    }
  }
  clickHandlerUser = (event) => {
    let regexPassword=/^(?=.*\d)(?=.*[A-Z])(?=.*[a-z])(?=.*[a-zA-Z!#$@^%&? "])[a-zA-Z0-9!#$@^%&?]{8,20}$/;
    let regexPhone=/^\d{10}$/;
    let regexEmail=/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/;
    if (event.target.innerHTML === "Sign Up") {
        let newEmail=this.refNewEmail.current.value;
        let newPhone=this.refNewPhone.current.value;
        let newPassword=this.refNewPassword.current.value;
        if(newEmail==="" || newPassword===""|| newPhone===""){

        }
    }
  };
  clickHandlerUpIn=()=>{
    this.upIn=!this.upIn;
    if(this.upIn===true){
        this.setState({
            signUpId:"signUpHidden",
            logInId:"logIn"
        })
    }
    else{
        this.setState({
            signUpId:"signUp",
            logInId:"logInHidden"
        })
    }
  }
  ChangeHandlerInp=event=>{
    let regexPassword=/^(?=.*\d)(?=.*[A-Z])(?=.*[a-z])(?=.*[a-zA-Z!#$@^%&? "])[a-zA-Z0-9!#$@^%&?]{8,20}$/;
    let regexPhone=/^\d{10}$/;
    let regexEmail=/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/;
    let id=event.target.getAttribute("id");
    let value=event.target.value;
    if(id==="newEmail"){
        if(regexEmail.test(value)!==true)
        this.setState({
            paraNewEmail:"Enter a valid email"
        })
    }
  }
  render() {
    return (
      <div id="containerSignLog">
         <div id="signLog">
         <div id={this.state.signUpId}>
            <p className="signLogHeading">Sign Up</p>
            <div>
              <input onChange={this.ChangeHandlerInp} ref={this.refNewEmail} placeholder="Enter your email" type="email" id="newEmail"/><p className="paraError"></p>
              <input onChange={this.ChangeHandlerInp} ref={this.refNewPhone} placeholder="Enter your number" type="number" id="newPhone"/><p className="paraError"></p>
              <input onChange={this.ChangeHandlerInp} ref={this.refNewPassword} placeholder="Enter new password" type="password" id="newPassword"/><p className="paraError"></p>
            </div>
            <button id="newSubmit" onClick={this.clickHandlerUser}>
              Sign Up
            </button>
            <p className="paraLink" >Already registered. <a onClick={this.clickHandlerUpIn}> Log In.</a></p>
          </div>
          <div id={this.state.logInId} >
            <p className="signLogHeading" >Log In</p>
            <div>
              <input placeholder="Enter email or phone number" id="newEmailPhone"/><p className="paraError">Enter valid phone or email</p>
              <input placeholder="Enter your password" type="password" id="oldPassword"/><p className="paraError">Enter valid password</p>

            </div>
            <button id="OldSubmit" onClick={this.clickHandlerUser}>
              Log In
            </button>
            <p className="paraLink">New User? <a onClick={this.clickHandlerUpIn}> Sign Up.</a></p>
          </div>
        </div>
      </div>
    );
  }
}

export default SignLog;
